package paper;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Array : when we have multiple value for same data type then array is used 
		int a[] = new int[5];
		a[0] = 2;
		a[1] = 4;
		a[2] = 7;
		a[3] = 10;
		a[4] = 11;
		for(int i=0;i<a.length-1;i++)
		{
			System.out.println(a[i]);
		}
		
	}

}
